text:
função com um argumento
::: {.cell}
# declarando
atualizar <- function(histórico) {
    atual <- ((histórico + 17)/2)
    return(atual)
}
# usando um escalar
atualizar(1)
# usando com um vetor
atualizar(c(1:15))
:::
função com dois argumentos
::: {.cell}
# declarando
ajustar <- function(valor1, valor2) {
    ajuste <- ((valor1 + 180)/(valor2 - 60))
    return(ajuste)
}
# usando com escalares
ajustar(100, 80)
# usando com vetores
ajustar(c(0,1,2), c(61, 62, 63)) 
# usando com vetor  e escalar
ajustar(c(0,1,2), 61)
:::





if
::: {.cell}
valor <- 100000
if (valor >= 1000000) {
    "número grande"
}
:::
if e else
::: {.cell}
valor <- 100000
if (valor >= 1000000) {
    ret <- "número grande"
} else {
    ret <- "número pequeno"
}
:::
ternário
::: {.cell}
valor <- 100000
ret <- if (valor >= 1000000) "número grande" else "número pequeno"        
:::
múltiplas condições
::: {.cell}
valor <- 650000

if (valor >= 1000000) {
    ret <- "número grande"
} else if (valor >= 500000 & valor <1000000) {
    ret <- "número intermediário"
} else {
    ret <- "número pequeno"
}
:::





for
::: {.cell}
for (i in c(1:15)) {
    print((i + 17)/2)
}
:::
while
::: {.cell}
valores <- 2
while(valores < 100){
    valores <- (valores + 20)
    print(valores)
}
:::
